import React, { useEffect, useState } from "react";
import Card from '../components/Cards';
//import { useState } from "react";

const Home = () => {
    const [planets, setPlanets] = useState([]);
    const [vehicles, setVehicles] = useState([]);
    const [people, setPeople] = useState([]);

    //Planetas
    useEffect(() => {
        fetch("https://swapi.dev/api/planets/", {
            method: "GET",
            headers: { "Content-Type": "application/json" }
        }).then(response => response.json())
            .then(data => setPlanets(data.results))
    }, [])

    //Vehicle
    useEffect(() => {
        fetch("https://swapi.dev/api/vehicles/", {
            method: "GET",
            headers: { "Content-Type": "application/json" }
        }).then(response => response.json())
            .then(data => setVehicles(data.results))
    }, [])

    //People
    useEffect(() => {
        fetch("https://swapi.dev/api/people/", {
            method: "GET",
            headers: { "Content-Type": "application/json" }
        }).then(response => response.json())
            .then(data => setPeople(data.results))
    }, [])

    return (
        <>
            <div class="container-fluid">
                <h1>Planets</h1>
                <div className="scrolling-wrapper row flex-row flex-nowrap mt-3 pb-3 pt-3">
               {planets.map((planet, i) =><div className="col-md-4"> <Card data={planet} /></div>)}   
                </div>
            </div>

            <div class="container-fluid">
                <h1>Vehicles</h1>
                <div className="scrolling-wrapper row flex-row flex-nowrap mt-3 pb-3 pt-3">
               {vehicles.map((planet, i) =><div className="col-md-4"> <Card data={vehicle} /></div>)}   
                </div>
            </div>

            <h1>Vehicles</h1>
            <div>{vehicles.map((vehicle, i) => <Card data={vehicle} />)}</div>

            <h1>Peoples</h1>
            <div>{people.map((people, i) => <Card data={people} />)}</div>
        </>
    );
}


export default Home;
