import React from 'react';
import { Link } from "react-router-dom";
import { useState, useEffect } from 'react';


const Planetsinfo = ( ) => {

    const [planets, setPlanets] = useState({});

    useEffect(() => {
        fetch("https://www.swapi.tech/api/planets", {
            method: "GET",
            headers: { "Content-Type": "application/json" }
        }).then(response => response.json())
            .then(data => setPlanets(data.result.results))
    },[])


    return (
        <div className="card">


            <div className="card-body">
                <img className="card-img-top" src={planets.image} alt="Card image cap" />

                <h5 className="card-title">{planets.data.name}</h5>
                <p className="card-text">Terrain: {planets.data.terrain}</p>
                <p className="card-text">{planets.data.url}</p>
                <Link to="#" className="btn btn-primary">Learn more</Link>
                <button className="btn btn-outline-warning"><i class="bi bi-heart"></i></button>
            </div>

        </div>
    )

}



export default Planetsinfo;
