export const getState = ({ setStore, getStore, getActions }) => {

    return {
        store: {
           planets: [] 
        },
        actions: {
           getPlanets: () => {
            fetch("https://swapi.dev/api/planets/", {
                method: "GET",
                headers: { "Content-Type": "application/json" }
            }).then(response => response.json())
                .then(data => setStore({ planets:data.results }))
           }
        }
    };

}
